mapper.insert:
INSERT INTO TBL_PRODUCT_FILE
    (
        UUID, UPLOAD_PATH, FILE_NAME, PRODUCT_NUM
    )
VALUES
    (
        #{uuid},#{uploadPath},#{fileName},#{productNum}
    )

mapper.select:
SELECT UUID, UPLOAD_PATH, FILE_NAME, PRODUCT_NUM
FROM TBL_PRODUCT_FILE
WHERE PRODUCT_NUM=#{productNum}
