mapper.select:
SELECT COUNT(NUM)
FROM TBL_PROJECT
WHERE COMPANY_NUM = #{companyNum}

mapper.update:
UPDATE TBL_PROJECT
SET
TITLE = #{title}, CONTENT = #{content}, COURSE = #{course}, TERRAIN = #{terrain}, POINT = #{point}, APPROVAL = #{approval}, STARTTIME = #{startTime} , ENDTIME = #{endTime},
START_ADDRESS = #{startAddress}, END_ADDRESS = #{endAddress}, DISTANCE = #{distance}, START_QR = #{qr}, UPDATE_DATE = SYSDATE, STATUS = #{status}
WHERE NUM = #{num}

update TBL_PROJECT
set JJIM_COUNT = JJIM_COUNT+1
WHERE NUM = #{projectNum}

update TBL_PROJECT
set JJIM_COUNT = JJIM_COUNT-1
WHERE NUM = #{projectNum}

UPDATE TBL_PROJECT
SET APPROVAL='0'
WHERE NUM=#{num}

UPDATE TBL_PROJECT
SET APPROVAL='1'
WHERE NUM=#{num}

UPDATE TBL_PROJECT
SET APPROVAL='2'
WHERE NUM=#{num}

UPDATE TBL_PROJECT
SET APPROVAL='5'
WHERE NUM=#{num}
