mapper.select:
SELECT COUNT(NUM)
FROM TBL_APPLY app
WHERE app.PROJECT_NUM = #{projectNum}

mapper.update:
UPDATE TBL_APPLY
SET APPROACH='4'
WHERE NUM=#{applynum}

UPDATE TBL_APPLY
SET APPROACH='2'
WHERE NUM=#{applynum}

UPDATE TBL_APPLY
SET APPROACH='1', START_TIME= SYSDATE
WHERE USER_NUM = #{userNum} AND PROJECT_NUM = #{projectNum}

UPDATE TBL_APPLY
SET APPROACH='2', END_TIME = SYSDATE
WHERE USER_NUM = #{userNum} AND PROJECT_NUM = #{projectNum}

UPDATE TBL_APPLY
SET APPROACH='4'
WHERE NUM = #{num}
