mapper.select:
SELECT NUM, EMAIL, PASSWORD, NICKNAME, PHONE, BUSINESS_PHONE, ADDRESS, BUSINESS_NUMBER, REGIST_DATE, PROFILE_FILE_NAME, PROFILE_UPLOAD_PATH,CATEGORY,STATUS,APPROVAL
FROM TBL_COMPANY
WHERE NUM=#{num}

SELECT NUM, NICKNAME
FROM TBL_COMPANY
WHERE EMAIL=#{email} AND PASSWORD=#{password}

mapper.update:
UPDATE TBL_COMPANY
SET PASSWORD=#{password}
WHERE NUM=#{num}

UPDATE TBL_COMPANY
SET STATUS = '1'
WHERE NUM=#{num}

UPDATE TBL_COMPANY
SET STATUS = '0'
WHERE NUM=#{num}

UPDATE TBL_COMPANY
SET APPROVAL = '1'
WHERE NUM=#{num}

UPDATE TBL_COMPANY
SET APPROVAL = '0'
WHERE NUM=#{num}
