mapper.select:
SELECT bnumber, btitle, bcontent, bdate, unum, bimage
FROM board
WHERE bnumber = #{bnumber}

SELECT fname, foriginal, bnumber
FROM file
WHERE bnumber = #{bnumber}

mapper.update:
UPDATE BOARD
SET btitle=#{btitle}, bcontent=#{bcontent}, bimage=#{bimage}
WHERE bnumber = #{bnumber}

update reply 
set rcontent = #{rcontent} 
where rnumber = #{rnumber}
